INSERT INTO `automail` VALUES (1,'NewPassword','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>','','%afirstname% %asurname% <%aemail%>','OPUS: Account information for University of Ulster placement system','Sent to users on creation, or when a password is changed','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the Placement Management System at the University of Ulster.\r\n\r\nYou are receiving this message either because a new password has just been generated for your account or because an account has just been created for you.\r\n\r\nTo use the web based system, please go to\r\n\r\n%conf_website%\r\n           \r\nand click on the option to login. When prompted please use the credentials shown below.\r\n\r\nusername : %username%\r\npassword : %password%\r\n\r\nOnce you login you will be able to change your password if you wish. Please feel free to ask for any further guidance.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',1),(1,'StudentOnPlaced','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>',NULL,'%afirstname% %asurname% <%aemail%>','Congratulations, you have been placed','Sent to a student to confirm placed','Dear %rfirstname%,\r\n\r\nCongratulations, you have been successfully placed. Please see the details below for more information.\r\n\r\nCompany    : %pcompany%\r\nPosition   : %pposition%\r\nStart      : %pstart%\r\nEnd        : %pend%\r\nSupervisor : %psupervisor%\r\n\r\nPlease fill in any information missing above on the website as soon as possible.\r\n\r\nContinue to check the placement website\r\n\r\n%website%\r\n\r\nfor information on your assessment programme and academic tutor as it becomes available.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',2),(1,'CompanyOnClosed','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>','','','UU OPUS: One of the your vacancies has closed','Sent to listed contact when a vacancy closes','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the industrial placement system for %conf_institution%:\r\n\r\n%conf_website%\r\n\r\nOne of your vacancies listed on our system has just closed. If you wish to reopen it, please set a new closing date and change the status to \"open\" to allow automated applications, or \"special\" if you only allow applications via some other means.\r\n\r\nVacancy\r\n-------\r\n\r\n%custom_vacancydesc%\r\n\r\nCompany\r\n-------\r\n\r\n%custom_companyname%\r\n\r\nThere are currently %custom_applicationcount% applicants.\r\n\r\nYou can edit your vacancy (should you for example wish to reopen it) here\r\n\r\n%conf_website%/opus/%custom_editurl%\r\n\r\nor look at your applicants here\r\n\r\n%conf_website%/opus/%custom_manageurl%\r\n\r\nor just log in normally to otherwise maintain your account.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',3),(1,'NewPassword_Contact','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>','','%afirstname% %asurname% <%aemail%>','UU OPUS : Your account details for managing placement recruitment','Sent to company HR contacts upon account creation','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the Placement Management System at the University of Ulster. You will be able to use this account to examine your job advertisments and monitor student applications.\r\n\r\nYou are receiving this message either because a new password has just been generated for your account or because an account has just been created for you.\r\n\r\nTo use the web based system, please go to\r\n\r\n%conf_website%\r\n           \r\nand click on the option to login. When prompted please use the credentials shown below.\r\n\r\nusername : %username%\r\npassword : %password%\r\n\r\nOnce you login you will be able to change your password if you wish. Please feel free to ask for any further guidance.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',5),(1,'NewPassword_Staff','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>','','%afirstname% %asurname% <%aemail%>','OPUS: Your account details','New password sent to Academic Staff','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the Placement Management System at the University of Ulster. You can use this account to see details on the students you are allocated, as well as to record their assessment.\r\n\r\nYou are receiving this message either because a new password has just been generated for your account or because an account has just been created for you.\r\n\r\nTo use the web based system, please go to\r\n\r\nhttp://%conf_website%\r\n           \r\nand click on the option to login. When prompted please use the credentials shown below.\r\n\r\nusername : %username%\r\npassword : %password%\r\n\r\nOnce you login you will be able to change your password if you wish. Please feel free to ask for any further guidance.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',6),(1,'NewPassword_Supervisor','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>',NULL,'%afirstname% %asurname% <%aemail%>','Your account details for supervising your placement student','New Password Information for Workplace Supervisors','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the Placement Management System at the University of Ulster. This account is to help you supervise one placement student; you may receive more than one set of details if you are looking after more than one student - so please retain any other username and password you may already have.\r\n\r\nYou can use this account to see details recorded about the placement, contact staff in the university who can assist with any issues, and often to assess the student at an appropriate time in the academic year when you are requested to do so.\r\n\r\nWe appreciate all your help with our placement operation.\r\n\r\nYou are receiving this message either because a new password has just been generated for your account or because an account has just been created for you.\r\n\r\nTo use the web based system, please go to\r\n\r\nhttp://%conf_website%\r\n           \r\nand click on the option to login. When prompted please use the credentials shown below.\r\n\r\nusername : %username%\r\npassword : %password%\r\n\r\nOnce you login you will be able to change your password if you wish. Please feel free to ask for any further guidance.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%',7),(1,'PhoneHome_Install','%afirstname% %asurname% <%aemail%>','opus-stats@foss.ulster.ac.uk','','','New OPUS installation','This is used to send an email to UU upon installation, only with your permission','New OPUS install\r\n\r\ninstitution: %conf_institution%\r\nurl: %conf_website%\r\nversion: %conf_version%\r\n',8),(1,'PhoneHome_Periodic','%afirstname% %asurname% <%aemail%>','opus-stats@foss.ulster.ac.uk','','','OPUS Periodic Statistics','Non confidential information, send periodically with your consent, to the University of Ulster','institution: %conf_institution%\r\nurl: %conf_website%\r\nversion: %conf_version%\r\n\r\nstudents: %custom_students%\r\nroots: %custom_roots%\r\nadmins: %custom_admins%\r\ncontacts: %custom_contacts%\r\nstaff: %custom_staff%\r\nsupervisors: %custom_supervisors%\r\ncompanies: %custom_companies%\r\nvacancies: %custom_vacancies%\r\n',9);

insert into `automail` (language_id, lookup, fromh, toh, cch, bcch, subject, description, contents) values (1,'NewPassword_Student','%afirstname% %asurname% <%aemail%>','%rfirstname% %rsurname% <%remail%>','','%afirstname% %asurname% <%aemail%>','Your account details for OPUS','Sent to new Student users on creation (if configured)','Dear %rtitle% %rsurname%,\r\n\r\nThis is an automated message from the OPUS Placement Management System at the University of Ulster. You can now login to begin looking for placements.\r\n\r\nYou are receiving this message either because a new password has just been generated for your account or because an account has just been created for you.\r\n\r\nTo use the web based system, please go to\r\n\r\nhttp://%conf_website%\r\n           \r\nand click on the option to login. When prompted please use the credentials shown below.\r\n\r\nusername : %username%\r\npassword : %password%\r\n\r\nOnce you login you will be able to change your password if you wish. Please feel free to ask for any further guidance.\r\n\r\n%atitle% %afirstname% %asurname%\r\n%aposition%');

